const video = document.querySelector('.video-container video');
const playButton = document.querySelector('.play-button');
const videoOverlay = document.querySelector('.video-overlay');

playButton.addEventListener('click', () => {
    if (video.paused) {
        video.play();
        playButton.style.backgroundImage = "url('data:image/png;base64,')"; // Change to pause icon
    } else {
        video.pause();
        playButton.style.backgroundImage = "url('data:image/png;base64,')"; // Change to play icon
    }
    videoOverlay.style.display = "none"; // Hide the overlay when the video plays/pauses
});

// Optional: Hide the overlay when the video starts playing (if the user uses default controls)
video.addEventListener('play', () => {
    videoOverlay.style.display = "none";
});

video.addEventListener('pause', () => {
    videoOverlay.style.display = "flex"; // Show overlay when paused
});

video.addEventListener('ended', () => {
    videoOverlay.style.display = "flex"; // Show overlay at the end
    playButton.style.backgroundImage = "url('data:image/png;base64,')"; // Reset to play icon
});


let currentIndex = 0;
const images = document.querySelectorAll('.carousel-image');

function showSlide(index) {
    images.forEach((img, i) => {
        img.style.display = i === index ? 'block' : 'none';
    });
}

function prevSlide() {
    currentIndex = (currentIndex === 0) ? images.length - 1 : currentIndex - 1;
    showSlide(currentIndex);
}

function nextSlide() {
    currentIndex = (currentIndex === images.length - 1) ? 0 : currentIndex + 1;
    showSlide(currentIndex);
}

// Auto Slide (Optional)
setInterval(nextSlide, 3000); // Change slide every 3 seconds

const carousel2 = document.querySelector(".carousel-2");

function autoScroll() {
  if (carousel2.scrollLeft + carousel2.clientWidth >= carousel2.scrollWidth) {
    carousel2.scrollLeft = 0;
  } else {
    carousel2.scrollLeft += carousel2.clientWidth / 3; // Scroll by one image
  }
}

