

function showImages(type) {
    const allImages = document.querySelectorAll('#carousel-1 img');
    allImages.forEach(img => img.classList.add('d-none'));

    let imageSources = {};

    imageSources['engagement'] = [
        "data:image/jpeg;base64,", 
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR8MBT9U1-N0BM2N1Q613_orh9-FBsGMawN5w&s",
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSXrfsT7zTJctc_EPmZSQClgdFByRRBFPfQ5A&s"
    ];
    imageSources['him'] = [
        'data:image/jpeg;base64,', // Path to your image file
        'data:image/jpeg;base64,',
        'data:image/jpeg;base64,'
    ];
    imageSources['platinum'] = [
        'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT9o8sT4ebsoCbKR7K7pUN5_IGPed2qBRlClA&s', // Path to your image file
        'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS4e37fM8vlw_W2ABJpS2nWosA76nj7Lh-W5A&s',
        'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRExK4emyiNGkDU0q1n7cY-nmOkmNCpPh0prQ&s'
    ];

    if (imageSources[type]) {  // Check if sources exist for this type
        for (let i = 0; i < 3; i++) {
            const imageElement = document.getElementById(`image${i + 1}`);
            imageElement.src = imageSources[type][i];
            imageElement.classList.remove('d-none');
        }
    }
}

